import React, { useState } from 'react';
import { View, Text, TextInput, Button } from 'react-native';

const Postagens = () => {
  const [novaPostagem, setNovaPostagem] = useState('');

  const handleCriarPostagem = () => {
    // Aqui você pode implementar a lógica para enviar a nova postagem para o servidor
    // Por enquanto, estamos apenas imprimindo a nova postagem no console
    console.log('Nova postagem:', novaPostagem);
    // Limpar o campo de texto após criar a postagem
    setNovaPostagem('');
  };

  return (
    <View style={{ padding: 20 }}>
      <Text style={{ fontSize: 20, marginBottom: 10 }}>Nova Postagem</Text>
      <TextInput
        style={{ borderWidth: 1, borderColor: '#ccc', padding: 10, marginBottom: 10 }}
        placeholder="Digite sua postagem"
        value={novaPostagem}
        onChangeText={text => setNovaPostagem(text)}
      />
      <Button title="Criar Postagem" onPress={handleCriarPostagem} />
      {/* Aqui você pode renderizar as postagens existentes */}
    </View>
  );
};

export default Postagens;
